insert into documents(url,title,publication_date,modified_date,author,publisher,affected_donors,affected_donees,document_scope,cause_area,notes) values
  ('http://effective-altruism.com/ea/14w/2017_ai_risk_literature_review_and_charity/','2017 AI Risk Literature Review and Charity Comparison','2016-12-13',NULL,'Ben Hoskin',NULL,'Ben Hoskin','Machine Intelligence Research Institute,Future of Humanity Institute,OpenAI,Center for Human-Compatible AI,Future of Life Institute,Centre for the Study of Existential Risk,Leverhulme Centre for the Future of Intelligence,Global Catastrophic Risks Institute,Global Priorities Project,AI Impacts,Xrisks Institute,X-Risks Net,Center for Applied Rationality,80000 Hours,Raising for Effective Giving','Review of current state of cause area','AI risk','The lengthy blog post covers all the published work of prominent organizations focused on AI risk. References http://www.openphilanthropy.org/focus/global-catastrophic-risks/potential-risks-advanced-artificial-intelligence/machine-intelligence-research-institute-general-support#sources1007 for the MIRI part of it but notes the absence of information on the many other orgs'),
  ('https://www.centreforeffectivealtruism.org/blog/changes-in-funding-in-the-ai-safety-field','Changes in funding in the AI safety field','2017-02-01','2017-02-02','Sebastian Farquhar','Centre for Effective Altruism',NULL,'Machine Intelligence Research Institute,Center for Human-Compatible AI,Leverhulme Centre for the Future of Intelligence,Future of Life Institute,Future of Humanity Institute,OpenAI,MIT Media Lab','Review of current state of cause area','AI risk','The post reviews AI safety funding from 2014 to 2017 (projections for 2017). Cross-posted on EA Forum at http://effective-altruism.com/ea/16s/changes_in_funding_in_the_ai_safety_field/');
