insert into documents(url,title,publication_date,modified_date,author,publisher,affected_donors,affected_donees,document_scope,cause_area,notes) values
  ('https://www.openphilanthropy.org/blog/explaining-our-bet-sherlock-biosciences-innovations-viral-diagnostics','Explaining Our Bet on Sherlock Biosciences’ Innovations in Viral Diagnostics','2019-06-10',NULL,'Heather Youngs|Chris Somerville','Open Philanthropy','Open Philanthropy','Sherlock Biosciences','Single donation documentation','Scientific research','In this new-style blog post, the reasons for the Open Philanthropy Project grant https://www.openphilanthropy.org/focus/scientific-research/miscellaneous/sherlock-biosciences-research-viral-diagnostics to Sherlock Biosciences are explained in a conversational style. The conversation participants include Michael Levine (Communications Officer) and the grant investigators Chris Somerville and Heather Youngs.');
