/* Biosecurity and pandemic preparedness */

/* Johns Hopkins Center for Health Security  */

insert into donations(donor,donee,amount,donation_date,donation_date_precision,donation_date_basis,cause_area,url,donor_cause_area_url,influencer,donation_announcement_date,donation_announcement_date_precision,donation_announcement_url,predictions,donation_process,intended_use_of_funds_category,intended_use_of_funds,intended_funding_timeframe_in_months,donor_donee_reason,donor_amount_reason,donor_timing_reason,donor_next_donation_thoughts,donor_retrospective,notes) values
  ('Open Philanthropy','Johns Hopkins Center for Health Security',2744000,'2016-10-01','month','donation log','Biosecurity and pandemic preparedness','https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity/upmc-center-health-security-emerging-leaders-biosecurity-initiative','https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity','Jaime Yassif','2016-10-12','day','https://groups.google.com/a/openphilanthropy.org/forum/#!topic/newly.published/NXqvn6Yzlv0',NULL,
  /* donation_process */ NULL,
  /* intended_use_of_funds_category */ 'Direct project expenses',
  /* intended_use_of_funds */ 'Grant to support the continuation of the Emerging Leaders in Biosecurity Initiative (ELBI). For the first five years of its program, ELBI was funded by the Department of Defense (DoD). The grant page says: "Our understanding is that, for reasons unrelated to the quality of the program, DoD is not planning to renew support for it this year, and that the possibility of future DoD funding for the program is uncertain."',
  /*intended_funding_timeframe_in_months */ 36,
  /* donor_donee_reason */ 'The grant page says: "Our highly positive impression of ELBI is based in part on the opinion of Jaime Yassif (our Program Officer for Biosecurity and Pandemic Preparedness), in part on our observation that the program has a strong reputation throughout the field, and in part on our favorable view of the Johns Hopkins Center for Health Security, which runs the program. Jaime is a 2012 alumna of ELBI."',
  /* donor_amount_reason */ 'Likely to be based on the cost of the program',
  /* donor_timing_reason */ 'The grant page says: "Our understanding is that, for reasons unrelated to the quality of the program, DoD is not planning to renew support for it this year, and that the possibility of future DoD funding for the program is uncertain."',
  /* donor_next_donation_thoughts */ NULL,
  /*donor_retrospective */ NULL,
  /* notes */ 'The recipient was housed at the time at the University of Pittsburgh Medical Center (UPMC) and called the UPMC Center for Health Security. The grant was made to fully support the Emerging Leaders in Biosecurity Initiative (ELBI) for the next three years. The grant was based partly on the positive impression of the program by Jaime Yassif, program officer in the area who was an alumnus from 2012'),
  ('Open Philanthropy','Johns Hopkins Center for Health Security',16000000,'2017-01-01','month','donation log','Biosecurity and pandemic preparedness','https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity/center-health-security-biosecurity-global-health-security-and-global-catastrophic','https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity', 'Jaime Yassif', '2017-02-08', 'day', 'https://groups.google.com/a/openphilanthropy.org/forum/#!topic/newly.published/Lqvk_2mJCIE',NULL,
  /* donation_process */ 'According to https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity/center-health-security-biosecurity-global-health-security-and-global-catastrophic#Our_process "Jaime had several conversations with CHS leadership about high-level issues relevant to the grant, reviewed materials shared by CHS, and spoke to other experts in the field to get their perspectives on its work."',
  /* intended_use_of_funds_category */ 'Organizational general support',
  /* intended_use_of_funds */ 'Grant to support CHS''s work on biosecurity, global health security, and global catastrophic risks posed by pathogens. Over the course of the grant, CHS plans to devote about one-third of its total funding and staff time to GCR-related projects and two-thirds to general health security and public health preparedness work',
  /* intended_funding_timeframe_in_months */ 36,
  /* donor_donee_reason */ 'According to https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity/center-health-security-biosecurity-global-health-security-and-global-catastrophic#The_organization the grantee organization has (1) Track record of research and policy development, (2) Track record of policy impact',
  /* donor_amount_reason */ 'According to https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity/center-health-security-biosecurity-global-health-security-and-global-catastrophic#Budget_and_room_for_more_funding "We do not have a detailed budget breakdown for the grant; it is mostly unrestricted and is designed to give CHS flexibility to pursue the projects it considers most important and to have the most impact." An approximate breakdown is given. Also: "Overall, we estimate that this grant will increase CHS’s annual budget from $5.3 million to approximately $8 million."',
  /* donor_timing_reason */ NULL,
  /* donor_next_donation_thoughts */ NULL,
  /* donor_retrospective */ 'The September 2019 renewal https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity/center-health-security-biosecurity-health-security-gcrs-2019 (3 years, for $19.5 million) suggests that Open Phil would be satisfied with the results of the grant',
  /* notes */ 'Largest grant made to date by Open Phil. Grant writeup includes lengthy discussion of grant. Open Phil had previously made a grant to the organization when it was housed at the University of Pittsburgh Medical Center (UPMC) and known as the UPMC Center for Health Security. Using the grant money, the grantee would launch a bunch of projects related to Global Catastrophic Biological Risks (GCBR); see http://www.centerforhealthsecurity.org/about-the-center/pressroom/press_releases/2017-07-27_global-catastrophic-biological-risk-definition.html (2017-07-27) for the associated press release'),

  ('Open Philanthropy','Johns Hopkins Center for Health Security',169600,'2018-06-01','month','donation log','Biosecurity and pandemic preparedness','https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity/upmc-center-health-security-synbiobeta-2018-meeting','https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity','Jaime Yassif','2018-07-26','day',NULL,NULL,
  /* donation_process */ 'Discretionary grant',
  /* intended_use_of_funds_category */ 'Direct project expenses',
  /* intended_use_of_funds */ '$127,600 to Johns Hopkins Center for Health Security and $42,000 to SynBioBeta to support a biosecurity fellowship program and a biosecurity panel discussion at the 2018 SynBioBeta conference https://2018.synbiobeta.com/ on synthetic biology.',
  /* intended_funding_timeframe_in_months */ NULL,
  /* donor_donee_reason */ NULL,
  /* donor_amount_reason */ NULL,
  /* donor_timing_reason */ NULL,
  /* donor_next_donation_thoughts */ NULL,
  /* donor_retrospective */ NULL,
  /* notes */ NULL),

  ('Open Philanthropy','Johns Hopkins Center for Health Security',19500000,'2019-09-01','month','donation log','Biosecurity and pandemic preparedness','https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity/center-health-security-biosecurity-health-security-gcrs-2019','https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity','Andrew Snyder-Beattie','2019-10-04','day',NULL,NULL, /* date adjusted */
  /* donation_process */ NULL,
  /* intended_use_of_funds_category */ 'Organizational general support',
  /* intended_use_of_funds */ 'Grant "to support work on biosecurity, global catastrophic risks posed by pathogens, and other work related to CHS’s mission, and to support the Emerging Leaders in Biosecurity Initiative. CHS plans to use these funds to continue to conduct policy research and continue to build communications and advocacy capacity."',
  /* intended_funding_timeframe_in_months */ 36,
  /* donor_donee_reason */ NULL,
  /* donor_amount_reason */ 'No explicit reason for amount given, but it is similar to the previous three-year support amount of $16 million',
  /* donor_timing_reason */ 'Timing likely determined by the fact that the timeframe for the previous three-year grant (starting January 2017) is coming to an end',
  /* donor_next_donation_thoughts */ NULL,
  /* donor_retrospective */ NULL,
  /* notes */ NULL),

  ('Open Philanthropy','Johns Hopkins Center for Health Security',1860000,'2020-02-01','month','donation log','Biosecurity and pandemic preparedness','https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity/johns-hopkins-center-health-security-masters-phd-program-support','https://www.openphilanthropy.org/focus/global-catastrophic-risks/biosecurity','Andrew Snyder-Beattie','2020-03-23','day',NULL,NULL, /* date adjusted */
  /* donation_process */ NULL,
  /* intended_use_of_funds_category */ 'Direct project expenses',
  /* intended_use_of_funds */ 'Grant "to support the implementation of a Masters and PhD program. The program will focus on major biological and health security risks. This funding will support four PhD students for four years each and four masters students for one year each, as well as faculty time for advising students and a junior administrator."',
  /* intended_funding_timeframe_in_months */ 48,
  /* donor_donee_reason */ NULL,
  /* donor_amount_reason */ NULL,
  /* donor_timing_reason */ NULL,
